import React from "react";
import { Link } from "react-router-dom";

function SideBar({user, dispatch }){
    return (
        <>
          <div className="col-lg-2 p-2 bg-dark white" style={{ backgroundColor: "#000", fontSize:"24px", height:"100vh" }}>
              <div className="top_section">
                <h1 className="logo p-2" style={{color: "white"}}>
                  IKAA
                </h1>
              </div>
              <div className="bottomSection">
                <Link className="link" activeclassname="active" to={"/login"}>
                  <div className="icon p-2">
                    DashBoard
                  </div>
                </Link>
    
                {user?.userType === "patient" ? (
                  <Link
                    className="link"
                    activeclassname="active"
                    to={"/patientprofile"}
                  >
                    <div className="p-2">
                      Profile
                    </div>
                  </Link>
                ) : null}
    
                {user?.userType === "patient" ? (
                  <Link
                    className="link"
                    activeclassname="active"
                    to={"/bookappointment"}
                  >
                    <div className="p-2">
                      Book Appointment
                    </div>
                  </Link>
                ) : null}
                {user?.userType === "admin" ? (
                  <Link className="link" activeclassname="active" to={"/dsignin"}>
                    <div className="p-2">
                      Add Doctor
                    </div>
                  </Link>
                ) : null}
                {user?.userType === "admin" ? (
                  <Link className="link" activeclassname="active" to={"/addnurse"}>
                    <div className="p-2">
                      Add Nurse
                    </div>
                  </Link>
                ) : null}
                {user?.userType === "admin" ? (
                  <Link className="link" activeclassname="active" to={"/addAdmin"}>
                    <div className="p-2">
                      Add Admin
                    </div>
                  </Link>
                ) : null}
    
                {user?.userType === "doctor" ? (
                  <Link
                    className="link"
                    activeclassname="active"
                    to={"/doctorprofile"}
                  >
                    <div className="p-2">
                      Profile
                    </div>
                  </Link>
                ) : null}
                {user?.userType === "doctor" ? (
                  <Link
                    className="link"
                    activeclassname="active"
                    to={"/checkappointment"}
                  >
                    <div className="p-2">
                      Appointments
                    </div>
                  </Link>
                ) : null}
                {user?.userType === "doctor" ? (
                  <Link
                    className="link"
                    activeclassname="active"
                    to={"/createslip"}
                  >
                    <div className="p-2">
                      Create Report
                    </div>
                  </Link>
                ) : null}
                {/* {user?.userType === "doctor" ? (
                  <Link
                    className="link"
                    activeclassname="active"
                    to={"/patientdetails"}
                  >
                    <div className="icon">
                      <TbListDetails className="mainIcon" />
                    </div>
                    <div
                      style={{ display: isOpen ? "block" : "none" }}
                      className="link_text"
                    >
                      Patients
                    </div>
                  </Link>
                ) : null} */}
    
                <Link
                  className="LogOutPath link"
                  onClick={() => {
                    dispatch({ type: "AUTH_LOGOUT" });
                  }}
                  to={"/"}
                >
                  <div className="p-2">
                    Logout
                  </div>
                </Link>
              </div>
            </div>
        </>
      );
    };
    
export default SideBar;
