import React from 'react';
import {connect} from 'react-redux';
const axios = require('axios');

class UserProfile extends React.Component {
    constructor(props){
        super(props);
        this.state={
            firstname:this.props.firstname,
            username:this.props.username,
            email:this.props.email,
            gender:this.props.gender,
        }
    }

    fetchUserDetails=(email)=>{
        //console.log(user_id);
        axios.get("http://localhost:5000/userapi/getUserDetails/"+email,{
            headers: {
                "content-type": "application/json"
              }
        }).then(res=>{
            console.log(res);
            this.setState({email:res.data.results[0].email});
            this.setState({username:res.data.results[0].username})
        })
        .catch(err=>console.log(err))
    }

    // UpdateProfileHandler=(e)=>{
    //     e.preventDefault();
    //     //create object of form data
    //     const formData=new FormData();
    //     formData.append("profileImage",this.state.uploadedFile);
    //     formData.append("user_id",this.state.user_id);

    //     //update-profile
    //     axios.post("http://localhost:5000/userapi/update-profile/",formData,{
    //         headers: {
    //             "content-type": "application/json"
    //           }
    //     }).then(res=>{
    //         console.log(res);
    //        this.setState({msg:res.data.message});
    //        this.setState({profileImage:res.data.results.profileImage});
    //     })
    //     .catch(err=>console.log(err))
    // }


    componentDidMount(){
     this.fetchUserDetails(this.state.email);
    }

render(){

    // if(this.state.profileImage){
    //     var imagestr=this.state.profileImage;
    //     imagestr = imagestr.replace("public/", "");
    //     var profilePic="http://localhost:5000/"+imagestr;
    // }else{
    //      profilePic=DefaultUserPic;
    // }

    return (
        <div className="row">
       {/* <Col>
       <img src={profilePic} alt="profils pic" />
       </Col> */}
        <div className="col">
            <h1>User Profile</h1>
            <Form className="form">     
  <Form.Group controlId="formCategory1">
    <Form.Label>Username</Form.Label>
    <Form.Control type="text" defaultValue={this.state.username}/>
  
  </Form.Group>
  <Form.Group controlId="formCategory2">
    <Form.Label>Email</Form.Label>
    <Form.Control type="email" defaultValue={this.state.email} />
  
  </Form.Group>
 
  {/* <Form.Group controlId="formCategory4">
    <Form.Label>Profile Image</Form.Label>
    <Form.Control type="file" name="profileImage" onChange={this.changeProfileImage}/>
    </Form.Group> */}
  <Button variant="primary" onClick={this.UpdateProfileHandler}>Update Profile</Button>
  </Form>
   </div>

       </div>
        
    )
}
}

const mapStatetoProps=(state)=>{
    return{
        firstname:state.user.userDetails.firstname,
        username:state.user.userDetails.username,
       email:state.user.email,
       gender: state.user.gender,
    //    msg:state.user.msg
    }
   }
   
   

export default connect(mapStatetoProps)(UserProfile);
