import React,{useState} from "react";

function Login(){
    const [selectedRole, setSelectedRole] = useState("");
    const [doctorLogin, setDoctorLogin] = useState(false);
    
    const handleRoleSelection = (role) => {
        setSelectedRole(role);
        // Perform actions based on the selected role (e.g., redirect to a specific page or display role-specific content)
        switch (role) {
        case 'doctor':
            // Logic for doctor login
            setDoctorLogin(true);
            break;
        case 'admin':
            // Logic for admin login
            <admin/>
            break;
        case 'patient':
            // Logic for patient login
            <patient/>
            break;
        default:
            break;
        }
    };

    return(
        <div className="container-fluid">
            <div className="row">
                <div 
                    className="col-lg-6"
                    style={{
                        height: "100vh",
                        backgroundImage: `url(Images/banner.png)`,
                        backgroundSize: "cover",
                        backgroundPosition: "center",
                          }}
                ></div>
                <div className="col-lg-6 " style={{ textAlign: 'center', backgroundColor: 'orange'}}>
                    <h2>Login</h2>
                    <p>Please select your designation:</p>
                    <div>
                        <button onClick={() => handleRoleSelection('doctor')}>Doctor</button>
                        <button onClick={() => handleRoleSelection('admin')}>Admin</button>
                        <button onClick={() => handleRoleSelection('patient')}>Patient</button>
                    </div>
                    {selectedRole && <p>Selected Role: {selectedRole}</p>}
                    {/* You can display role-specific content or perform actions based on the selected role */}
                    {doctorLogin &&(
                        <div className="container d-flex align-items-center justify-content-center" style={{width:'500px', height:'300px'}}>
                             <div className="wow fadeIn ">
                                <form className="mx-auto">
                                    <div className="form-group">
                                        <label className="mb-1" style={{color: 'black'}}>Username</label>
                                        <input type="text" class="form-control border-0 text-center " placeholder="Username" style={{height: '65px', width:'300px'}}/>
                                    </div>
                                    <div className="form-group">
                                        <label className="mt-3 mb-1" style={{color: 'black'}}>Password</label>
                                        <input type="password " class="form-control text-center border-0 mb-3" placeholder="Password" style={{height: '65px', width:'300px'}}/>
                                    </div>
                                    <p style={{color: 'black'}}>Don't Have An Account? <a href="SignIn.html">SignIn</a></p>
                                </form>
                                <a className="btn btn-dark w-100 p-3" href="doctor_page.html">Login</a>
                                <p style={{color: 'black'}}>Forgot Password? <a href="SignIn.html">Forgot</a></p>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    )
}

export default Login;
