import React, { useState } from "react";
import axios from "axios";

function BookAppointment() {
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        appointmentDate: "",
        paymentMethod: "",
        cardNumber: "",
        expiryDate: "",
        cvv: "",
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const res = await axios.post("http://localhost:5000/bookAppointment", formData);

            if (res.data === "success") {
                alert("Appointment booked successfully!");
            } else {
                alert("Failed to book the appointment. Please try again.");
            }
        } catch (error) {
            alert("Error booking the appointment");
            console.error("Error:", error);
        }
    };

    return (
        <div style={{ backgroundColor: "rgba(131, 220, 255, 0.29)", height: "100vh" }}>
            <div className="container " style={{ backgroundColor: "cornSilk", fontSize:"20px", padding: "20px" }}>
                <div className="p-3">
                    <h1>Add Admin</h1>
                    <form onSubmit={handleSubmit}>
                        <div>
                            <label className="p-4">Name:</label>
                            <input type="text" name="name" value={formData.name} onChange={handleChange} />
                        </div>
                        <div>
                            <label>Email:</label>
                            <input type="email" name="email" value={formData.email} onChange={handleChange} />
                        </div>
                        <div>
                            <label>Appointment Date:</label>
                            <input type="date" name="appointmentDate" value={formData.appointmentDate} onChange={handleChange} />
                        </div>
                        <div>
                            <label>Payment Method:</label>
                            <select name="paymentMethod" value={formData.paymentMethod} onChange={handleChange}>
                                <option value="">Select Payment Method</option>
                                <option value="creditCard">Credit Card</option>
                                <option value="debitCard">Debit Card</option>
                            </select>
                        </div>
                        {formData.paymentMethod && (
                            <div>
                                <label>Card Number:</label>
                                <input type="text" name="cardNumber" value={formData.cardNumber} onChange={handleChange} />
                                <label>Expiry Date:</label>
                                <input type="text" name="expiryDate" value={formData.expiryDate} onChange={handleChange} placeholder="MM/YY" />
                                <label>CVV:</label>
                                <input type="text" name="cvv" value={formData.cvv} onChange={handleChange} />
                            </div>
                        )}
                        <button type="submit">Book Appointment</button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default BookAppointment;
