import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";

function Profile() {
  const { isAuthenticated, user, getAccessTokenSilently } = useAuth0();
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        if (isAuthenticated) {
          const accessToken = await getAccessTokenSilently();

          // Make a request to your backend API to fetch user-specific data
          const response = await fetch("mongodb://localhost:27017/mainDatabase",patientCollection, {
            headers: {
              Authorization: `Bearer ${accessToken}`,
            },
          });

          if (!response.ok) {
            throw new Error("Failed to fetch user data");
          }

          const userData = await response.json();
          setUserData(userData);
        }
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    fetchUserData();
  }, [isAuthenticated, getAccessTokenSilently]);

  return (
    <div>
      {isAuthenticated && userData && (
        // Render your user profile based on the retrieved userData
        <div>
          <h1>Welcome, {user.name}!</h1>
          <p>Email: {user.email}</p>
          {/* Display other user-specific data from 'userData' */}
          {/* Example: <p>First Name: {userData.firstname}</p> */}
        </div>
      )}
    </div>
  );
}

export default Profile;
