import React from 'react';
import { connect } from 'react-redux';
import axios from 'axios';

class UserProfile extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        firstname:this.props.firstname,
        username:this.props.username,
        email:this.props.email,
        gender:this.props.gender,
    };
  }

  fetchUserDetails = (email) => {
    axios.get(`http://localhost:5000/getUserDetails/${email}`, {
        headers: {
          "content-type": "application/json"
        }
      })
      .then(res => {
        console.log(res);
        if (res.data.results && res.data.results.length > 0) {
          const user = res.data.results[0];
          this.setState({
            email: user.email,
            username: user.username
          });
        } else {
          console.log("No user found"); // Add appropriate error handling here
        }
      })
      .catch(err => console.log(err));
  }

  componentDidMount() {
    this.fetchUserDetails(this.props.email);
  }

  render() {
    return (
      <div className="row">
        <div className="col">
          <h1>User Profile</h1>
          <form className="form">
            <div className="form-group">
              <label htmlFor="username">Username</label>
              <input type="text" className="form-control" id="username" value={this.state.username} readOnly />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input type="email" className="form-control" id="email" value={this.state.email} readOnly />
            </div>
            <button type="button" className="btn btn-primary" onClick={this.UpdateProfileHandler}>Update Profile</button>
          </form>
        </div>
      </div>
    )
  }
}

const mapStatetoProps = (state) => {
  return {
        firstname:state.user.userDetails.firstname,
        username:state.user.userDetails.username,
        email:state.user.email,
        gender: state.user.gender,
  }
}

export default connect(mapStatetoProps)(UserProfile);
