import React, { useRef } from "react";

function Testimonials() {
    const carouselRef = useRef(null);

    const slideNext = () => {
        carouselRef.current.next();
    };

    const slidePrev = () => {
        carouselRef.current.prev();
    };
    return (
        <div>
            <h1 p-3>What Our Patients Say</h1>
            <div id="Testimonials" className="text-center">
                <div className="owl-carousel testimonial-carousel">
                    <div className="owl-carousel-item">
                        {/* <!-- Testimonial 1 --> */}
                        <img className="img-fluid bg-light rounded-circle p-2 mx-auto m-4" src="Images/harvey-specter-tile.png" alt="..." style={{ width: '150px', height: '150px' }} />
                        <div>
                            <h2>The best care ever. Recently discharged after the treatment of Brain Tumor. had no confidence of survival but here I am!!</h2>
                            <em>Harvey Specter, FA</em>
                        </div>
                    </div>
                    <div className="owl-carousel-item">
                        {/* <!-- Testimonial 2 --> */}
                        <img className="img-fluid bg-light rounded-circle p-2 mx-auto mb-4" src="Images/mike-ross.png" alt="..." style={{ width: '150px', height: '150px' }} />
                        <div>
                            <h2>Never feels like being in a hospital with doctor but in my own house with my family. Great Support.</h2>
                            <em>Mike Ross, Manhattan</em>
                        </div>
                    </div>
                    <div className="owl-carousel-item">
                        {/* <!-- Testimonial 3 --> */}
                        <img className="img-fluid bg-light rounded-circle p-2 mx-auto mb-4" src="Images/don.png" alt="..." style={{ width: '150px', height: '150px' }} />
                        <div>
                            <h2>Very Skilled and efficient doctors with a huge success rate. Never felt better!</h2>
                            <em>Don Coreleone, LA</em>
                        </div>
                    </div>
                </div>
                <div className="custom-nav">
                    <button className="custom-prev-btn m-2" onClick={slidePrev}>
                        {/* Add an arrow icon for previous */}
                        {/* Example: <i className="fa fa-arrow-left"></i> */}
                        Previous
                    </button>
                    <button className="custom-next-btn" onClick={slideNext}>
                        {/* Add an arrow icon for next */}
                        {/* Example: <i className="fa fa-arrow-right"></i> */}
                        Next
                    </button>
                </div>
            </div>
        </div>
    )
}

export default Testimonials;
